CREATE TABLE `sms`.`smsdata` ( `id` INT(10) NOT NULL AUTO_INCREMENT , `number` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL , `sender` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL , `msg` TEXT CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL , `msg_id` VARCHAR(191) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
